    // Manejo del evento de mouse
    private void txtRfcProvKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtRfcProvKeyPressed
        if (evt.getKeyCode() == evt.VK_ENTER) {
            txtNombreProv.requestFocus();
        }
    }//GEN-LAST:event_txtRfcProvKeyPressed

    private void txtNombreProvKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtNombreProvKeyPressed
        if (evt.getKeyCode() == evt.VK_ENTER) {
            txtTelefonoProv.requestFocus();
        }
    }//GEN-LAST:event_txtNombreProvKeyPressed

    private void txtTelefonoProvKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtTelefonoProvKeyPressed
        if (evt.getKeyCode() == evt.VK_ENTER) {
            txtDireccionProv.requestFocus();
        }
    }//GEN-LAST:event_txtTelefonoProvKeyPressed

