    private void txtCodigoProKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtCodigoProKeyPressed
        if (evt.getKeyCode() == evt.VK_ENTER) {
            txtDescripcionPro.requestFocus();
        }
    }//GEN-LAST:event_txtCodigoProKeyPressed

